client/src/components/usage-limit-banner.content:
import { Alert, AlertDescription } from "@/components/ui/alert";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { useUsageLimit } from "@/hooks/useUsageLimit";
import { AlertTriangle, Infinity, Users } from "lucide-react";
import { useLocation } from "wouter";

export default function UsageLimitBanner() {
  const { usageInfo, isLoading } = useUsageLimit();
  const [, setLocation] = useLocation();

  if (isLoading || !usageInfo) {
    return null;
  }

  const handleSignup = () => {
    setLocation("/signup");
  };

  const handleLogin = () => {
    setLocation("/login");
  };

  const { isAuthenticated, remainingTranslations, limitMessage } = usageInfo;

  if (isAuthenticated) {
    // Show bronze badge for authenticated users
    return (
      <div className="mb-4">
        <Alert className="border-green-200 bg-green-50 dark:border-green-800 dark:bg-green-950">
          <Infinity className="h-4 w-4 text-green-600" />
          <AlertDescription className="flex items-center justify-between">
            <div className="flex items-center">
              <span className="text-green-800 dark:text-green-200 mr-2">
                You are now on bronze tier!
              </span>
              <Badge className="bg-green-600">Bronze</Badge>
            </div>
          </AlertDescription>
        </Alert>
      </div>
    );
  }

  // Show limit information for guest users
  const isLowOnTranslations =
    remainingTranslations <= 1 && remainingTranslations > 0;
  const isAtLimit = remainingTranslations === 0;

  if (remainingTranslations > 1) {
    // Show remaining count when user has multiple translations left
    return (
      <div className="mb-4">
        <Alert className="border-blue-200 bg-blue-50 dark:border-blue-800 dark:bg-blue-950">
          <AlertDescription className="flex items-center justify-between">
            <div>
              <span className="text-blue-800 dark:text-blue-200">
                You have{" "}
                <strong>{remainingTranslations} free translations</strong>{" "}
                remaining today.
              </span>
            </div>
            <Button
              size="sm"
              variant="default"
              onClick={handleLogin}
              data-testid="button-upgrade-banner"
              className="border-blue-300 text-blue-700 hover:bg-blue-100 dark:border-blue-600 dark:text-blue-300 dark:hover:bg-blue-900"
            >
              <Users className="w-4 h-4 mr-1" />
              Get more
            </Button>
          </AlertDescription>
        </Alert>
      </div>
    );
  }

  if (isLowOnTranslations) {
    // Show warning when down to last translation
    return (
      <div className="mb-4">
        <Alert className="border-orange-200 bg-orange-50 dark:border-orange-800 dark:bg-orange-950">
          <AlertTriangle className="h-4 w-4 text-orange-600" />
          <AlertDescription className="flex items-center justify-between">
            <div>
              <span className="text-orange-800 dark:text-orange-200 font-medium">
                Last free translation!
              </span>
              <span className="text-orange-700 dark:text-orange-300 ml-2">
                Sign up for bronze access.
              </span>
            </div>
            <Button
              size="sm"
              onClick={handleSignup}
              data-testid="button-upgrade-last"
              className="bg-orange-600 hover:bg-orange-700"
            >
              <Users className="w-4 h-4 mr-1" />
              Sign Up Now
            </Button>
          </AlertDescription>
        </Alert>
      </div>
    );
  }

  if (isAtLimit) {
    // Show limit reached message
    return (
      <div className="mb-4">
        <Alert className="border-red-200 bg-red-50 dark:border-red-800 dark:bg-red-950">
          <AlertTriangle className="h-4 w-4 text-red-600" />
          <AlertDescription className="flex items-center justify-between">
            <div className="flex-1">
              <div className="text-red-800 dark:text-red-200 font-medium mb-1">
                Daily limit reached
              </div>
              <div className="text-red-700 dark:text-red-300 text-sm">
                {limitMessage}
              </div>
            </div>
            <div className="flex items-center space-x-2 ml-4">
              <Button
                onClick={handleLogin}
                data-testid="button-login-limit"
                variant="outline"
                className="border-red-300 text-red-700 hover:bg-red-100 dark:border-red-600 dark:text-red-300 dark:hover:bg-red-900"
              >
                Login
              </Button>
              <Button
                onClick={handleSignup}
                data-testid="button-signup-limit"
                className="bg-red-600 hover:bg-red-700"
              >
                <Users className="w-4 h-4 mr-1" />
                Sign Up
              </Button>
            </div>
          </AlertDescription>
        </Alert>
      </div>
    );
  }

  return null;
}

client/src/components/feedback-dialog.ts.content:
import { useState } from 'react';
import { Dialog, DialogContent, DialogHeader, DialogTitle } from '@/components/ui/dialog';
import { Button } from '@/components/ui/button';
import { Label } from '@/components/ui/label';
import { Textarea } from '@/components/ui/textarea';
import { Star, MessageSquare } from 'lucide-react';
import { useForm } from 'react-hook-form';
import { zodResolver } from '@hookform/resolvers/zod';
import { z } from 'zod';
import { useMutation } from '@tanstack/react-query';
import { apiRequest } from '@/lib/queryClient';
import { useToast } from '@/hooks/use-toast';
import type { SubmitFeedbackRequest } from '@shared/schema';

const feedbackFormSchema = z.object({
  starRating: z.number().int().min(1, "Please select a rating").max(5, "Rating cannot exceed 5 stars"),
  feedbackMessage: z.string().max(1000, "Feedback message must be less than 1000 characters").optional(),
});

type FeedbackFormData = z.infer<typeof feedbackFormSchema>;

interface FeedbackDialogProps {
  open: boolean;
  onOpenChange: (open: boolean) => void;
  onSubmit?: () => void;
  onSkip?: () => void;
}

export function FeedbackDialog({ open, onOpenChange, onSubmit, onSkip }: FeedbackDialogProps) {
  const [hoveredStar, setHoveredStar] = useState<number>(0);
  const { toast } = useToast();

  const {
    register,
    handleSubmit,
    watch,
    setValue,
    formState: { errors },
    reset,
  } = useForm<FeedbackFormData>({
    resolver: zodResolver(feedbackFormSchema),
    defaultValues: {
      starRating: 0,
      feedbackMessage: "",
    },
  });

  const starRating = watch('starRating');

  const submitFeedbackMutation = useMutation({
    mutationFn: async (data: SubmitFeedbackRequest) => {
      return apiRequest('POST', '/api/feedback', data);
    },
    onSuccess: () => {
      toast({
        title: "Thank you for your feedback!",
        description: "Your feedback helps us improve ECHO.",
        variant: "default",
      });
      reset();
      onOpenChange(false);
      onSubmit?.();
    },
    onError: (error: any) => {
      console.error('Feedback submission error:', error);
      toast({
        title: "Failed to submit feedback",
        description: "Please try again later.",
        variant: "destructive",
      });
    },
  });

  const onFormSubmit = (data: FeedbackFormData) => {
    submitFeedbackMutation.mutate({
      starRating: data.starRating,
      feedbackMessage: data.feedbackMessage?.trim() || undefined,
    });
  };

  const handleStarClick = (rating: number) => {
    setValue('starRating', rating, { shouldValidate: true });
  };

  const handleClose = () => {
    reset();
    onOpenChange(false);
    onSkip?.();
  };

  return (
    <Dialog open={open} onOpenChange={onOpenChange}>
      <DialogContent className="sm:max-w-md bg-white dark:bg-gray-900 border-2 border-amber-200 dark:border-amber-800">
        <DialogHeader>
          <DialogTitle className="text-amber-900 dark:text-amber-100 flex items-center gap-2">
            <MessageSquare className="h-5 w-5" />
            Share Your Feedback
          </DialogTitle>
        </DialogHeader>

        <form onSubmit={handleSubmit(onFormSubmit)} className="space-y-6 py-4">
          <div className="space-y-3">
            <Label className="text-amber-800 dark:text-amber-200 font-medium">
              How would you rate your experience?
            </Label>

            {/* Star Rating */}
            <div className="flex items-center gap-1" data-testid="star-rating">
              {[1, 2, 3, 4, 5].map((star) => (
                <button
                  key={star}
                  type="button"
                  onClick={() => handleStarClick(star)}
                  onMouseEnter={() => setHoveredStar(star)}
                  onMouseLeave={() => setHoveredStar(0)}
                  className="p-1 rounded transition-colors hover:bg-amber-50 dark:hover:bg-amber-900/20"
                  data-testid={`button-star-${star}`}
                >
                  <Star
                    className={`h-8 w-8 transition-colors ${
                      star <= (hoveredStar || starRating)
                        ? 'fill-amber-400 text-amber-400'
                        : 'text-gray-300 dark:text-gray-600'
                    }`}
                  />
                </button>
              ))}
            </div>

            {errors.starRating && (
              <p className="text-sm text-red-600 dark:text-red-400" data-testid="error-star-rating">
                {errors.starRating.message}
              </p>
            )}
          </div>

          <div className="space-y-2">
            <Label htmlFor="feedbackMessage" className="text-amber-800 dark:text-amber-200 font-medium">
              Tell us more (optional)
            </Label>
            <Textarea
              id="feedbackMessage"
              placeholder="What did you like? What could we improve? Share any thoughts..."
              className="min-h-[100px] border-amber-200 dark:border-amber-700 focus:border-amber-400 dark:focus:border-amber-500"
              data-testid="input-feedback-message"
              {...register('feedbackMessage')}
            />
            {errors.feedbackMessage && (
              <p className="text-sm text-red-600 dark:text-red-400" data-testid="error-feedback-message">
                {errors.feedbackMessage.message}
              </p>
            )}
          </div>

          <div className="flex gap-3 pt-4">
            <Button 
              type="button"
              variant="outline" 
              onClick={handleClose}
              className="flex-1 border-amber-300 text-amber-700 hover:bg-amber-50 dark:border-amber-700 dark:text-amber-300 dark:hover:bg-amber-900/20"
              data-testid="button-skip-feedback"
              disabled={submitFeedbackMutation.isPending}
            >
              Skip
            </Button>
            <Button 
              type="submit"
              className="flex-1 bg-amber-600 hover:bg-amber-700 text-white dark:bg-amber-500 dark:hover:bg-amber-600"
              data-testid="button-submit-feedback"
              disabled={submitFeedbackMutation.isPending}
            >
              {submitFeedbackMutation.isPending ? 'Submitting...' : 'Submit Feedback'}
            </Button>
          </div>
        </form>
      </DialogContent>
    </Dialog>
  );
}

