@import url('https://fonts.googleapis.com/css2?family=Inter:wght@300;400;500;600;700&display=swap');
@tailwind base;
@tailwind components;
@tailwind utilities;

:root {
  /* Language learning themed color palette */
  --background: hsl(195, 35%, 98%);
  --foreground: hsl(210, 40%, 8%);
  --card: hsl(195, 25%, 95%);
  --card-foreground: hsl(210, 40%, 8%);
  --popover: hsl(195, 25%, 95%);
  --popover-foreground: hsl(210, 40%, 8%);

  /* Deep teal - enhances cognitive focus for language learning */
  --primary: hsl(175, 65%, 40%);
  --primary-foreground: hsl(195, 35%, 98%);

  /* Warm coral - encourages speaking and interaction */
  --secondary: hsl(15, 75%, 55%);
  --secondary-foreground: hsl(195, 35%, 98%);

  /* Soft reading backgrounds */
  --muted: hsl(195, 20%, 88%);
  --muted-foreground: hsl(210, 25%, 40%);

  /* Golden accent - celebrates progress and achievements */
  --accent: hsl(45, 85%, 58%);
  --accent-foreground: hsl(210, 40%, 8%);

  /* Gentle warning tones */
  --destructive: hsl(355, 70%, 55%);
  --destructive-foreground: hsl(195, 35%, 98%);

  /* Clean language-friendly borders */
  --border: hsl(195, 25%, 78%);
  --input: hsl(195, 20%, 91%);
  --ring: hsl(175, 65%, 40%);
  --radius: 16px;
  --font-sans: 'Inter', sans-serif;
}

.dark {
  /* Language learning dark theme */
  --background: hsl(210, 35%, 6%);
  --foreground: hsl(195, 25%, 94%);
  --card: hsl(210, 30%, 10%);
  --card-foreground: hsl(195, 25%, 94%);
  --popover: hsl(210, 30%, 10%);
  --popover-foreground: hsl(195, 25%, 94%);

  /* Soft teal for nighttime focus */
  --primary: hsl(175, 50%, 50%);
  --primary-foreground: hsl(210, 35%, 6%);

  /* Warm coral for gentle interaction */
  --secondary: hsl(15, 60%, 65%);
  --secondary-foreground: hsl(210, 35%, 6%);

  /* Comfortable dark reading */
  --muted: hsl(210, 25%, 16%);
  --muted-foreground: hsl(195, 20%, 75%);

  /* Golden motivation */
  --accent: hsl(45, 75%, 65%);
  --accent-foreground: hsl(210, 35%, 6%);

  /* Soft error indication */
  --destructive: hsl(355, 60%, 65%);
  --destructive-foreground: hsl(210, 35%, 6%);

  /* Dark UI elements */
  --border: hsl(210, 25%, 18%);
  --input: hsl(210, 25%, 14%);
  --ring: hsl(175, 50%, 50%);
}

@layer base {
  * {
    @apply border-border;
  }

  body {
    @apply font-sans antialiased bg-background text-foreground;
    /* Prevent zoom on iOS Safari */
    -webkit-text-size-adjust: 100%;
    /* Enhanced mobile scrolling */
